using Newtonsoft.Json.Linq;
using System.Net.Http.Json;
using System.Text;

/*
 * { productId: 'basico',
  transactionReceipt: '',
  transactionId: '2000000102372203',
  transactionDate: '1657532290000.0' }
 */
//https://stackoverflow.com/questions/22933133/verifying-ios-in-app-purchase-receipt-with-c-sharp

await PurchaseItem("");

async Task PurchaseItem(string receiptData)
{
    try
    {
        // var json = "{ 'receipt-data': '" + receiptData + "'}";

        //https://stackoverflow.com/questions/47944400/how-do-i-use-httpclient-postasync-parameters-properly

        var json = new JObject(new JProperty("receipt-data", receiptData)).ToString();
        ASCIIEncoding ascii = new ASCIIEncoding();
        byte[] postBytes = Encoding.UTF8.GetBytes(json);

        var client = new HttpClient();
        var content = JsonContent.Create(postBytes);
        HttpResponseMessage response = await client.PostAsync("https://sandbox.itunes.apple.com/verifyReceipt", content);
        response.EnsureSuccessStatusCode();

        // https://developer.apple.com/documentation/appstorereceipts/status
        string responseBody = await response.Content.ReadAsStringAsync();

        Console.WriteLine("Response: {0}", responseBody);
    }
    catch (Exception ex)
    {
        Console.WriteLine("Erro: {0}", ex.Message.ToString());
    }
}
